package com.zensar.data;

public class Data {
	
	private int[] arr;
	private int total;
	public Data() {
		// TODO Auto-generated constructor stub
	}
	
	public Data(int[] arr, int total) {
		super();
		this.arr = arr;
		this.total = total;
	}
	public int[] getArr() {
		return arr;
	}
	public void setArr(int[] arr) {
		this.arr = arr;
	}
	public int getTotal() {
		return total;
	}
	public void setTotal(int total) {
		this.total = total;
	}
	
	

}
